import React from 'react';

const CategorieList = [
    'React', 
    'JavaScript', 
    'HTML', 
    "CSS"
  ]; 


// props.setView(e.target.innerHTML)
const ListOfCategories = (props) => {
  function handleListCate (e){ 
    props.setView(e.target.innerHTML);
  }
    return (
      <>
        <ul className="ul-cat-list">
          {CategorieList.map((val) => (
            <li key={val}
              name={val}
             className="li-cat-list"
             onClick={handleListCate}
            >{val}</li>
          ))}
        </ul>
      </>
    );
  }; 

  export default ListOfCategories;
