import React from "react";
import myClassesArr from "./myClassesArr";
import Test from "./Test";

class MyClasses extends React.Component {
  constructor(props) {
    super();
    this.state = {
      classes: myClassesArr,
      view: "",
      type: "",
    };

    this.DeleteClasses = this.DeleteClasses.bind(this);
    // this.RendersTest = this.RendersTest.bind(this);
  }

  /// removing the classes
  DeleteClasses(index) {
    let list = this.state.classes;
    list.splice(index, 1);
    this.setState({ list });
  }

  //   RendersTest (index){
  //       let type = this.state.classes;
  //     console.log(item.class)
  //       this.setState({view: "Test"})
  //   }

  render() {
    const classes = this.state.classes;
    if (this.state.view === "Test") {
      return <Test type={this.state.type} />;
    }
    return (
      <div className="container">
        <div className="row">
          <div className="col-12">
            <h1>My Classes</h1>
          </div>
          <div className="col-12">
            <h3>You are taking {classes.length} classes</h3>
          </div>
        </div>
        <div className="row">
          {classes.map((item) => (
            <div key={item.num} className="col-4 ">
              <div className="card text-center">
                <div className="card-body">
                  <div className="card-title">{item.type}</div>
                  <div className="card-subtitle mb-2 text-muted">
                    {item.class}
                  </div>
                  <button
                    onClick={this.DeleteClasses}
                    className="btn btn-outline-success"
                  >
                    {" "}
                    Delete
                  </button>
                  <button
                    onClick={() =>
                      this.setState({
                        view: "Test",
                        type: item.type,
                      })
                    }
                    className="btn btn-outline-success"
                  >
                    {" "}
                    Test{" "}
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default MyClasses;
