import React from 'react'; 
import './category.css';
import CardList from './CardsList';
import imagecss from './../../images/css.png';
import imagejs from './../../images/javascript.jpeg';
import imageReact from './../../images/react-img.jpeg';
import './cards.css';

import image from './../../images/react-img.jpeg';


class CategoryMain extends React.Component { 
  constructor(props){ 
    super();
    this.state = { 
      categorieList: [
        'React', 
        'JavaScript', 
        'HTML', 
        "CSS"
      ], 
      view: "main"
    }
     this.handleCateList = this.handleCateList.bind(this);
  } 


  handleCateList = (e) => { 

       const target = e.target.innerHTML;  
    this.setState({view: target});
  }









  render () { 
const view = this.state.view;

if ( this.state.view === "main"){ 

} else if (view === 'React'){ 
  return ( 

    <>
    <div className="row justify-content-start cat-page ">
      <div className="col-4 cat-page-col  ">
        {/* <ListOfCategories/> */}
        <ul className="ul-cat-list">
        {this.state.categorieList.map((val) => (
          <li key={val}
          onClick={this.handleCateList}
           className="li-cat-list"
          >{val}</li>
        ))}
      </ul>
      </div>
      <div className="col-8">
        <div className='col'>
        <h2 className="text-center"> {view} </h2>
        <img className='react-images ' src={imageReact} alt="javascript"/>
        </div>
        <CardList listView={this.state.view } />
      </div>
    </div>
    </>

  )
}


else if (view === 'HTML'){ 

  return ( 

    <>
    <div className="row justify-content-start cat-page ">
      <div className="col-4 cat-page-col  ">
        {/* <ListOfCategories/> */}
        <ul className="ul-cat-list">
        {this.state.categorieList.map((val) => (
          <li key={val}
          onClick={this.handleCateList}
           className="li-cat-list"
          >{val}</li>
        ))}
      </ul>
      </div>
      <div className="col-8">
        <div className='col'>
        <h2 className="text-center"> {view} </h2>

        </div>
        <CardList listView={this.state.view }/>
      </div>
    </div>
    </>

  )
}

else if (view === 'JavaScript'){ 

  return ( 

    <>
    <div className="row justify-content-start cat-page ">
      <div className="col-4 cat-page-col  ">
        {/* <ListOfCategories/> */}
        <ul className="ul-cat-list">
        {this.state.categorieList.map((val) => (
          <li key={val}
          onClick={this.handleCateList}
           className="li-cat-list"
          >{val}</li>
        ))}
      </ul>
      </div>
      <div className="col-8">
        <div className='col'>
        <h2 className="text-center"> {view} </h2>
        <img className='js-images' src={imagejs} alt="javascript"/>
        </div>
        <CardList listView={this.state.view }/>
      </div>
    </div>
    </>

  )
}
else if (view === 'CSS'){ 
  return ( 

    <>
    <div className="row justify-content-start cat-page ">
      <div className="col-4 cat-page-col  ">
        {/* <ListOfCategories/> */}
        <ul className="ul-cat-list">
        {this.state.categorieList.map((val) => (
          <li key={val}
          onClick={this.handleCateList}
           className="li-cat-list"
          >{val}</li>
        ))}
      </ul>
      </div>
      <div className="col-8">
        <div className='col'>
        <h2 className="text-center"> {view} </h2>
        <img src={imagecss} alt='css-image' />
        </div>
        <CardList listView={this.state.view } />
      </div>
    </div>
    </>

  )
}
    return ( 

      <>
      <div className="row justify-content-start cat-page ">
        <div className="col-4 cat-page-col  ">
          {/* <ListOfCategories/> */}
          <ul className="ul-cat-list">
          {this.state.categorieList.map((val) => (
            <li key={val}
            onClick={this.handleCateList}
             className="li-cat-list"
            >{val}</li>
          ))}
        </ul>
        </div>
        <div className="col-8">
          <div className='col'>
          <h2 className="text-center"> Checkout our list and see what classes we have to offer </h2>

            <img src={image} alt=""/>

          </div>
        </div>
      </div>
      </>

    )
  }
}

export default CategoryMain;
