import "./home.css";
import React from "react";
import cssImg from './../../images/css.png';
import htmlImg from './../../images/javascript.jpeg';



const course = [
  {
    num: 4,
    class: "React",
    description:
      "Learn How To Use The React Application With JavaScript To Build & Maintain User Interfaces.", 
    learn: "Learn more", 
    img: "https://img.icons8.com/nolan/250/javascript.png",
  },
  {
    num: 3,
    class: "JavaScript",
    description:
      "Learn How To Use The React Application With JavaScript To Build & Maintain User Interfaces.",
      learn: "Learn more", 
      img: htmlImg ,  
    },
  {
    num: 1,
    class: "HTML",
    description:
      "bringing two or more numbers (or things) together to make a new total.",
      learn: "Learn more", 
      img: "https://img.icons8.com/nolan/250/html-filetype.png" ,   
    },
  {
    num: 2,
    class: "CSS",
    description:
      "bringing two or more numbers (or things) together to make a new total.",
      learn: "Learn more", 
      img: cssImg ,   
    },
];



const CardCourse = () => {
  return (
    <>
      <div className="row">
    { 
        course.map((course)=> ( 
            <div className="col-sm-3">
            <div className="card card-course">
            <img class="card-img-top" src={course.img} alt={course.class}/>
              <div className="card-body text-center">
                <h5 className="card-title"> {course.class} </h5>
                <p className="card-text"> {course.description} </p>
                <p className='card-text learn-more'> {course.learn} </p>
              </div>
            </div>
          </div>
        ))
    }
</div>
    </>
  );
};

export default CardCourse;
